mapper.select:
select count(*) count, nvl(trunc(avg(rating),1) , 0.0) avg 
from review
where res_no = #{no}

mapper.delete:
delete from r_like 
where rev_no = #{rev_no}
and mem_no = #{mem_no}

mapper.select:
		select count(mem_no) 
  		from r_like 
  		where rev_no = #{rev_no}
  		and mem_no = #{mem_no}

mapper.update:
update review
set like_cnt = like_cnt + 1
where no = #{rev_no}

update review
set like_cnt = like_cnt - 1
where no = #{rev_no}
