mapper.select:
select a.*
from (select rownum rr , b.*
	from (select * from restaurant 




			where address1 like '%'||#{keyword}||'%'





			and c_no = #{category}



	) b ) a



		where a.rr between #{start} and #{end}

select count(*) from restaurant
where address1 like '%'||#{keyword}||'%'
and c_no = #{category}

select count 
from count
where res_no = #{no}

select nvl(trunc(avg(rating),1),0.0) avg
from review
where res_no = #{no}

select count(*) count, nvl(trunc(avg(rating),1) , 0.0) avg 
from review
where res_no = #{no}

select z.name , z.c_name , z.count ,nvl(trunc(avg(w.rating),1),0.0) avg, z.no from (select y.* from (select rownum rr , x.* from (select t.no, t.c_no , c.name c_name, t.name , address1 , address2 , tel , hour , n.count from restaurant t join category c on t.c_no = c.no join count n on t.no = n.res_no
where c.no = #{category}
order by n.count desc ) x ) y where y.rr between 1 and 5 ) z left outer join review w on z.no = w.res_no group by z.name , z.c_name , z.count, z.no order by z.count desc
